CREATE TABLE "employees" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY UNIQUE PRIMARY KEY,
  "full_name" varchar ( 100 ),
  "email" varchar ( 50 ),
  "position" varchar ( 50 ),
  "department_id" int,
  "created_at" date,
  "created_by" int,
  "modified_at" date,
  "modified_by" int
);

CREATE TABLE "departments" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY UNIQUE PRIMARY KEY,
  "name" varchar ( 50 ),
  "manager_id" int,
  "created_at" date,
  "created_by" int,
  "modified_at" date,
  "modified_by" int
);


CREATE TABLE "users" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY UNIQUE PRIMARY KEY,
  "username" varchar ( 100 ) not null,
  "password" varchar ( 100 ) not null,
  "role" varchar ( 50 ) not null
);








